<?php
defined('BASEPATH') OR exit('No direct script access allowed');
// require_once (APPPATH . 'libraries/Aes.php');

class shop extends MY_Controller {
	public $sandbox=1;
	function __construct() {
		parent::__construct ();
	}
	public function index() {

		//用户发来的参数
		$receipt_data = @$_POST['receipt-data'];
		$receipt_data="";
		//验证参数
		if (strlen($receipt_data)<20){
			$echo = array('buy'=>'0','message'=>'非法参数');
			exit(json_encode($echo));
		}
		//请求验证
		$data = $this->acurl($receipt_data,1);
		var_dump($data);die();
		//如果是沙盒数据 则验证沙盒模式
		if($data->status=='21007'){
			//请求验证
			$html = $this->acurl($receipt_data);
			$data = json_decode($html,1);
			$data['sandbox'] = '1';
		}
		if (isset($_GET['debug'])) {
			exit(json_encode($data));
		}
		if($data->status===0){
			$echo = array('buy'=>'1','message'=>'购买成功');
		}else{
			$echo = array('buy'=>'0','message'=>'购买失败','status'=>$data['status']);
		}
		//是否是沙盒模式
		if ($data['sandbox']) 
		$echo['sandbox'] = '1';
		echo json_encode($echo);
	}
	/**
  * 21000 App Store不能读取你提供的JSON对象
  * 21002 receipt-data域的数据有问题
  * 21003 receipt无法通过验证
  * 21004 提供的shared secret不匹配你账号中的shared secret
  * 21005 receipt服务器当前不可用
  * 21006 receipt合法，但是订阅已过期。服务器接收到这个状态码时，receipt数据仍然会解码并一起发送
  * 21007 receipt是Sandbox receipt，但却发送至生产系统的验证服务
  * 21008 receipt是生产receipt，但却发送至Sandbox环境的验证服务
 */
	function acurl($receipt_data){

		//小票信息
		$POSTFIELDS = array("receipt-data" => $receipt_data);
		$POSTFIELDS = json_encode($POSTFIELDS);

		//正式购买地址 沙盒购买地址
		$url_buy     = "https://buy.itunes.apple.com/verifyReceipt";
		$url_sandbox = "https://sandbox.itunes.apple.com/verifyReceipt";
		$url = ($this->sandbox==1) ? $url_sandbox : $url_buy;
		//简单的curl
		/*$ch = curl_init($url);
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
		curl_setopt($ch, CURLOPT_POST, 1);
		curl_setopt($ch, CURLOPT_POSTFIELDS, $POSTFIELDS);
		$result = curl_exec($ch);
		curl_close($ch);*/
		$curl_handle=curl_init();
		curl_setopt($curl_handle,CURLOPT_URL, $url);
		curl_setopt($curl_handle,CURLOPT_RETURNTRANSFER, true);
		curl_setopt($curl_handle,CURLOPT_HEADER, 0);
		curl_setopt($curl_handle,CURLOPT_POST, true);
		curl_setopt($curl_handle,CURLOPT_POSTFIELDS, $POSTFIELDS);
		curl_setopt($curl_handle,CURLOPT_SSL_VERIFYHOST, 0);
		curl_setopt($curl_handle,CURLOPT_SSL_VERIFYPEER, 0);
		$response_json =curl_exec($curl_handle);
		$response =json_decode($response_json);
		curl_close($curl_handle);
		return $response;
	}
}

